import React, { useEffect, useState } from 'react'
import { Index } from './View/Index'
import { Loading } from './View/Loading'
import axios from 'axios'

export const App = () => {
	const [getAnime, setGetAnime] = useState([])
	const [apiStatus, setApiStatus]= useState('')
	const [dataInfo, setDataInfo] = useState([])
	const [getSeach, setGetSeach] = useState('anime')
	const [loading, setLoading] = useState(false)

	const getAPI = async () => {
		// https://api.jikan.moe/v4/top/${getSeach}
		const result = await axios.get(`https://api.jikan.moe/v4/top/${getSeach}`)
		setGetAnime(result.data.data)
		setApiStatus(result.status)
	}
	console.log(apiStatus);
	const loadingHandle = () => {
		setLoading(true)
		while (apiStatus = 200) {
			setLoading(false)
		}
	}

	useEffect(() => {
		getAPI()
		loadingHandle()
	}, [])
	return (
		<div>
			<Loading loading={loading} />
			<Index getAnime={getAnime} getSeach={getSeach} setGetSeach={setGetSeach} loading={loading} />
		</div>
	)
}
