import React from 'react'
export const Card = ({ getAnime }) => {
    console.log(getAnime);
    return (
        <div className='flex justify-center items-start'>
            <div className='grid grid-cols-1 content-start items-center justify-center'>

                {getAnime.map((res, key) => {
                    return (
                        <div className='text-white max-w-xs  bg-indigo-900 my-5 rounded-md' key={key}>
                            <img src={res.images.jpg.image_url} className='rounded-t-md m-auto ' alt="" />
                            <div className='mx-3 my-6'>
                                <p>Title: {res.title}</p>
                                <p>Studios: {res.studios.name}</p>
                                <p>Score : {res.score}</p>
                                <p> Genres: 
                                    {res.genres.map((res, key) => {
                                        return (
                                            <span key={key}> {res.name}</span>
                                        )
                                    })}
                                </p>
                                <p>Producers: 
                                {res.producers.map((res, key) => {
                                    return (
                                        <span className='datalist' key={key}> {res.name} </span>
                                    )
                                })}
                                </p>
                            </div>
                        </div>
                    )
                })}

            </div>
        </div>
    )
}
