import React, { useEffect, useState } from 'react'
import { Index } from './View/Index'
import { findAllByTestId } from '@testing-library/react'
import { Loading } from './View/Loading'

export const App = () => {
	const [getAnime, setGetAnime] = useState([])
	const [dataInfo, setDataInfo] = useState([])
	const [getSeach, setGetSeach] = useState('anime')
	const [loading, setLoading] = useState(false)

	const getAPI = async() => {
		fetch(`https://api.jikan.moe/v4/top/${getSeach}`)
		.then((res) => res.json())
		.then((res) => setGetAnime(res.data))
	}

	const loadingHandle = () => {
		setLoading(true)
		while(getAnime.values !== dataInfo.values) {
			setLoading(false)
		}
	}

	useEffect(() => {
		getAPI()
		loadingHandle()
	}, [])
  return (
    <div>
		<Loading loading={loading}/>
	<Index getAnime={getAnime} getSeach={getSeach} setGetSeach={setGetSeach} loading={loading}/>
    </div>
  )
}
