import React, { useEffect, useState } from "react";
import { Index } from "./View/Index";
import { Loading } from "./View/Loading";
import axios from "axios";

export const App = () => {
	document.title = "AnimeList";
	const [getSeach, setGetSeach] = useState("anime");
	const [getAnime, setGetAnime] = useState([]);
	const [dataInfo, setDataInfo] = useState("");
	const [inputSearch, setInputSearch] = useState("");
	const [inputACC, setInputACC] = useState("")
	const [loading, setLoading] = useState(false);
	const [navHandle, setNavHandle] = useState(false);

	const apiDataHandler = () => {	
		// Udh Bener tolol
		setInputSearch(dataInfo)
		if (inputSearch  !== "") {
			console.log('search anime');
			const data = async() => {
				const result = await axios.get(`https://api.jikan.moe/v4/anime?q=${inputSearch}&sfw`)
				const dataStatus = result.status
				if(dataStatus === 200) {
					setGetAnime(result.data.data)
				} else {
					return alert('API ERROR ', dataStatus)
				}
			}
			data()

		}else if (inputSearch === '') {
			console.log('top anime');
			const getDataAPI = async () => {
				setLoading(true);
				const result = await axios.get(`https://api.jikan.moe/v4/top/${getSeach}`);
				setGetAnime(result.data.data);
				const apiStatus = result.status.toString();
				if (apiStatus === "200") {
					setLoading(false);
				} else {
					setLoading(true);
					alert(`ERROR API ${apiStatus}`);
				}
			};
			getDataAPI()
		}
		return
	};

	useEffect(() => {
		apiDataHandler()

	}, [getSeach, dataInfo]);

	return (
		<div>
			<Loading loading={loading} />
			<Index
				getAnime={getAnime}
				getSeach={getSeach}
				setGetSeach={setGetSeach}
				loading={loading}
				navHandle={navHandle}
				setNavHandle={setNavHandle}
				setInputSearch={setInputSearch}
				setDataInfo={setDataInfo}
				inputACC={inputACC}
				setInputACC={setInputACC}
				dataInfo={dataInfo}
			/>
		</div>
	);
};
