import React from "react";
import BounceLoader from "react-spinners/BounceLoader";

export const Loading = ({ loading }) => {
    return (
        <div className={`${loading ? 'block' : 'hidden'} min-h-screen`}>
            <BounceLoader
                color="rgb(99, 102, 241)"
                loading
                size={100}
                speedMultiplier={1}
            />
        </div>
    );
};
