import React, { useEffect, useState } from "react";
import { Index } from "./View/Index";
import { Loading } from "./View/Loading";
import axios from "axios";

export const App = () => {
  document.title = "AnimeList";
  const [getSeach, setGetSeach] = useState("anime");
  const [getAnime, setGetAnime] = useState([]);
  const [dataInfo, setDataInfo] = useState("");
  const [inputSearch, setInputSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [navHandle, setNavHandle] = useState(false);

  const apiDataHandler = () => {
    if (inputSearch !== "") {
      inputSearchHandler();
    } else {
      getDataAPI();
    }
  };

  const getDataAPI = async () => {
    setLoading(true);
    const result = await axios.get(`https://api.jikan.moe/v4/top/${getSeach}`);
    setGetAnime(result.data.data);
    const apiStatus = result.status.toString();
    if (apiStatus === "200") {
      setLoading(false);
    } else {
      setLoading(true);
      alert(`ERROR API ${apiStatus}`);
    }
  };

  const inputSearchHandler = () => {
    setInputSearch(dataInfo);
    const getDataSearch = async () => {
      const result = axios.get(
        `https://api.jikan.moe/v4/anime?q=${inputSearch}&sfw`
      );
      setGetAnime(result.data.data);
      const apiStatus = result.status.toString();
      if (apiStatus === "200") {
        setLoading(false);
      } else {
        setLoading(true);
        alert(`ERROR API ${apiStatus}`);
      }
    };
    getDataSearch();
  };

  const getAPI = async () => {
    // https://api.jikan.moe/v4/top/${getSeach}
    // setLoading(true)
    const result = await axios.get(`https://api.jikan.moe/v4/top/${getSeach}`);
    setGetAnime(result.data.data);
    let apiStatus = result.status.toString();
    if (apiStatus === "200") {
      setLoading(false);
    }
    if (apiStatus !== "200") {
      setLoading(true);
    }
  };

  useEffect(() => {
    getAPI();
  }, [getSeach]);

  return (
    <div>
      <Loading loading={loading} />
      <Index
        getAnime={getAnime}
        getSeach={getSeach}
        setGetSeach={setGetSeach}
        loading={loading}
        navHandle={navHandle}
        setNavHandle={setNavHandle}
        setInputSearch={setInputSearch}
        setDataInfo={setDataInfo}
      />
    </div>
  );
};
